import React, { useState, useEffect } from 'react';
import axios from 'axios';

function UserInfo() {
    const [userData, setUserData] = useState([]);

    useEffect(() => {
        // Retrieve user_id from local storage
        const user_id = localStorage.getItem('user_id');

        // Function to fetch user data from Laravel API
        const fetchData = async () => {
            try {
                const response = await axios.get(`http://127.0.0.1:8000/api/resource?user_id=${user_id}`);
                console.log(response.data);
                setUserData(response.data);
            } catch (error) {
                console.error('Error:', error);
            }
        };

        fetchData(); // Call the function when the component mounts
    }, []); // The empty array as the second argument ensures the effect runs only once

    return (
        <div>
            <h1>User List</h1>
            <ul>
                {userData.name}
                {Array.isArray(userData) && userData.map((user) => (
                    <li key={user.id}>
                        Name: {user.name}
                        Email: {user.email}
                    </li>
                ))}
            </ul>

        </div>
    );
}

export default UserInfo;
