import React, { useState } from 'react';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import axios from 'axios';

const LoginComponent = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');

    const handleLogin = async () => {
        try {
            const response = await axios.post('http://127.0.0.1:8000/api/user-login', {
                email: email,
                password: password,
            });

            if (response.data.token) {
                // Store the token in local storage
                localStorage.setItem('token', response.data.token);
                // Redirect to the dashboard page
                window.location.href = '/dashboard'; // Change this to the URL of your dashboard page
            } else {
                // Handle error or show success message
                console.log(response.data.message);
            }
        } catch (error) {
            // Handle login error
            console.error(error);
        }
    };


    return (
        <section className="login">
        <Container fluid>
            <>

                <Row className="row align-items-center d-flex">
                    <Col lg={5} sm={12} className="form_col">
                        <h2>Welcome To Your Trading Desk!</h2>
                        <form className="login_form">
                        <div className="input-div">
                            <input type="email" name={'email'} placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} />                
                        </div>
                        <div className="input-div">
                            <input type="password" name={'password'} placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} />                   
                        </div>

                        <button className="register-btn btn_form" onClick={handleLogin}>Login</button>
                        </form> 
                    </Col>
                    <Col lg={7} sm={12}>
                        <img src="assets/images/login_img.png" alt=""/>
                    </Col>
                </Row>

            </>
        </Container>
        </section>
    );
};

export default LoginComponent;
